import React from 'react';
import { StyleSheet, Text, View, Button} from 'react-native';

export default class Timer extends React.Component {

    public timerIsStart:boolean = false;
    public maxSecond: number = 60;

    state = {
        timer: 60,
        loop: 1,
        maxLoop: 4,
        exercises: [
            {
                name: 'Подтягивания',
                value: 1
            },
            {
                name: 'Приседания',
                value: 1
            },
            {
                name: 'Отжимания',
                value: 1
            },
            {
                name: 'Выпады',
                value: 1
            }
        ]
    }

    startTimer() {
        this.timerIsStart = true;
        this.timer();
    }

    resetTimer() {
        this.timerIsStart = false;
        this.setState({ timer: this.maxSecond });
    }

    timer = () => {
        let { timer } = this.state;

        const timerId = setInterval(() => {
            if ( timer > 0 && this.timerIsStart ) {
                timer -= 1;
                this.setState({ timer });
            } else if ( timer <= 0 || !this.timerIsStart ) {
                this.resetTimer();
                clearInterval(timerId);
            }
        },1000 * 1);
    }

    timerFormatter() {
        const { timer } = this.state;

        return (
            timer < 10 
                ? '0' + timer 
                : String(timer)
        );
    }

    render() {
        const { 
            loop, 
            maxLoop 
        } = this.state;
        const timerStr: string = this.timerFormatter();

        return (
            <View style={styles.container}>
                <View style={styles.item}>
                    <Text style={{ fontSize: 25, marginRight: '15px' }}>{`00:00:${timerStr}`}</Text>
                    <Text style={{ fontSize: 25 }}>{`${loop}/${maxLoop}`}</Text>
                </View>
                <View style={styles.item}>
                    <Button style={{ fontSize: 25, marginRight: '15px' }} onPress={() => {this.startTimer()}} title='Start' color='#841584' accessibilityLabel='Start timer'/>
                    <Button onPress={() => {this.resetTimer()}} title='Reset' color='#841584' accessibilityLabel='Reset timer'/>
                </View>
            </View>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: '#fff',
        alignItems: 'center',
        justifyContent: 'center',
    },
    item: {
        flexDirection: "row",
        flexWrap: "wrap",
        padding: "5px"
    }
});
