import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button} from 'react-native';

export default class App extends React.Component {

  state = {
    timerIsStart: false,
    timer: 20,
    loop: 1,
    loopMax: 4,
    exercises: [
      {
        name: 'Подтягивания',
        value: 1
      },
      {
        name: 'Приседания',
        value: 1
      },
      {
        name: 'Отжимания',
        value: 1
      },
      {
        name: 'Выпады',
        value: 1
      }
    ]
  }

  runTimer() {
    this.setState({ timerIsStart: true });
  }

  timer() {
    const { timer } = this.state;

    setTimeout(() => {
        if ( timer > 0 ) {
          this.setState({ timer: timer - 1 });
        }
      },
      1000
    );
  }

  render() {
    const { 
      timer, 
      timerIsStart 
    } = this.state;
    const timerStr: string = timer < 10 ? '0' + timer : String(timer);

    if ( timerIsStart && timer > 0 ) {
      this.timer();
    } else if ( timerIsStart && timer <= 0) {
      this.setState({
        timer: 20,
        timerIsStart: false 
      })
    }

    return (
      <View style={styles.container}>
        <Text>{`00:00:${timerStr}`}</Text>
        <Button onPress={this.runTimer}  title='Start'  color='#841584'  accessibilityLabel='Start timer'/>
        <StatusBar style='auto' />
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
