import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { StyleSheet, Text, View, Button} from 'react-native';

export default class Timer extends React.Component {

    public timerIsStart:boolean = false;
    public maxSecond: number = 60;
    public maxLoop: number = 4;

    state = {
        timer: 60,
        loop: 1,
        exercises: [
            {
                name: 'Подтягивания',
                value: 1
            },
            {
                name: 'Приседания',
                value: 1
            },
            {
                name: 'Отжимания',
                value: 1
            },
            {
                name: 'Выпады',
                value: 1
            }
        ]
    }

    startTimer() {
        this.timerIsStart = true;
        this.timer();
    }

    resetTimer() {
        this.timerIsStart = false;
        this.setState({ timer: this.maxSecond });
    }

    timer = () => {
        const { timer } = this.state;

        let timerId = setInterval(() => {
            debugger
            if ( timer > 0 && this.timerIsStart ) {
                this.setState({ timer: this.maxSecond - 1 });
            } else if ( timer <= 0 || !this.timerIsStart ) {
                this.resetTimer();
                clearInterval(timerId);
            }
        },1000 * 1);
    }

    timerFormatter() {
        const { timer } = this.state;

        return (
            timer < 10 
                ? '0' + timer 
                : String(timer)
        );
    }

    render() {
        const timerStr: string = this.timerFormatter();

        return (
            <View style={styles.container}>
                <Text>{`00:00:${timerStr}`}</Text>
                <Button onPress={() => {this.startTimer()}} title='Start' color='#841584' accessibilityLabel='Start timer'/>
                <Button onPress={() => {this.resetTimer()}} title='Reset' color='#841584' accessibilityLabel='Start timer'/>
            </View>
        );
    }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
